import { useState } from "react";
import { Square } from "./Square";

export default function Board() {
  // const [squares, setSquares] = useState(Array(9).fill(null));
  const [squares, setSquares] = useState([1, 3, 5, 7, 9, 11, 13, 15, 17]);

  function updateSquare() {
    const nextSquares = squares.slice();
    nextSquares[0] = "X";
    debugger;
    setSquares(nextSquares);

    // array ko index
    //tyo index ma k value rakhni
    //naya array square ma update garni
  }

  return (
    <>
      <div className="board-row">
        <Square value={squares[0]} onsetSquare={updateSquare} />
        <Square value={squares[1]} onsetSquare={updateSquare} />
        <Square value={squares[2]} onsetSquare={updateSquare} />
      </div>

      <div className="board-row">
        <Square value={squares[3]} onsetSquare={updateSquare} />
        <Square value={squares[4]} onsetSquare={updateSquare} />
        <Square value={squares[5]} onsetSquare={updateSquare} />
      </div>

      <div className="board-row">
        <Square value={squares[6]} onsetSquare={updateSquare} />
        <Square value={squares[7]} onsetSquare={updateSquare} />
        <Square value={squares[8]} onsetSquare={updateSquare} />
      </div>
    </>
  );
}
