import { useState } from "react";
import { Square } from "./Square";

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  // const [squares, setSquares] = useState([1, 3, 5, 7, 9, 11, 13, 15, 17]);

  function updateSquare(i) {
    const nextSquares = squares.slice();
    nextSquares[i] = "X";
    setSquares(nextSquares);

    // array ko index
    //tyo index ma k value rakhni
    //naya array square ma update garni
  }

  return (
    <>
      <div className="board-row">
        <Square value={squares[0]} onSquareClick={() => updateSquare(0)} />
        <Square value={squares[1]} onSquareClick={updateSquare} />
        <Square value={squares[2]} onSquareClick={updateSquare} />
      </div>

      <div className="board-row">
        <Square value={squares[3]} onSquareClick={updateSquare} />
        <Square value={squares[4]} onSquareClick={updateSquare} />
        <Square value={squares[5]} onSquareClick={updateSquare} />
      </div>

      <div className="board-row">
        <Square value={squares[6]} onSquareClick={updateSquare} />
        <Square value={squares[7]} onSquareClick={updateSquare} />
        <Square value={squares[8]} onSquareClick={updateSquare} />
      </div>
    </>
  );
}
